import UIKit
import WebKit

class MenuVC: UIViewController, WKUIDelegate, WKNavigationDelegate {

    
    private var webView: WKWebView!

    @IBOutlet weak var webViewOutlet: WKWebView!
    
    
    override func viewDidLoad() {
        super.viewDidLoad()
        
        let preferences = WKPreferences()
        preferences.javaScriptEnabled = true
        let configuration = WKWebViewConfiguration()
        configuration.preferences = preferences

        webView = WKWebView(frame: view.bounds, configuration: configuration)
        view.addSubview(webView)
        
        webViewOutlet.uiDelegate = self
        webViewOutlet.navigationDelegate = self
        
        
        var obsah = "<p><strong>Jako jediní v Česku zažíváme naprosto úžasné kolektivní vědomí skupiny! </strong></p>\n<h2>Bubnováním ve tmě získáte sami sebe!</h2>\n<p>Získává si však i vás, proto jej stále opakuje! 🙂</p>\n<p><span style=\"font-weight: 400;\">Stáváte se zde součástí hudby.  </span><span style=\"font-weight: 400;\">J</span><span style=\"font-weight: 400;\">ste v ní a ona je kolem vás. Je jedno zda buben držíte v ruce poprvé, nebo jste profesionál. </span><span style=\"font-weight: 400;\">Hudba ožívá a zabírá veškerý prostor, sjednocuje vědomí bubeníků.</span></p>\n<p><span style=\"font-weight: 400;\"><br />\n<strong>Máte možnost prožít kolektivní vědomí skupiny a své vědomí jako nedílnou součást celku!</strong></span></p>\n<p><a href=\"http://laskyplnysvet.cz/stesti/wp-content/uploads/2017/03/Bubnovani_ve_tme_divadlo_ABC_b.jpg\"><img class=\"aligncenter wp-image-2816 size-full\" src=\"http://laskyplnysvet.cz/stesti/wp-content/uploads/2017/03/Bubnovani_ve_tme_divadlo_ABC_b.jpg\" alt=\"\" width=\"960\" height=\"540\" srcset=\"https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/03/Bubnovani_ve_tme_divadlo_ABC_b.jpg 960w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/03/Bubnovani_ve_tme_divadlo_ABC_b-300x169.jpg 300w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/03/Bubnovani_ve_tme_divadlo_ABC_b-768x432.jpg 768w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/03/Bubnovani_ve_tme_divadlo_ABC_b-600x338.jpg 600w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/03/Bubnovani_ve_tme_divadlo_ABC_b-520x293.jpg 520w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/03/Bubnovani_ve_tme_divadlo_ABC_b-740x416.jpg 740w\" sizes=\"(max-width: 960px) 100vw, 960px\" /></a></p>\n<p> </p>\n<p>Bubnování ve tmě je naprosto odlišné od běžného bubnování se zavřenýma očima.</p>\n<h3>Stačí pouze zavřít oči?</h3>\n<p>Můžete se tím příjemně uzavřít před okolním světem a hudbu prožívat svým duchem, zkoumat sebe a to, jak na vás hudba působí.</p>\n<p>Je to velmi příjemná zkušenost, ale můžete ji dát nový rozměr &#8211; pojďme do toho!</p>\n<h3>Každé bubnování ve tmě je unikátní a jedinečné</h3>\n<p>Vždy se sejdou jiní lidé, kteří vytvoří jiný rytmus a tím ožije hudba jiným vědomím. Vždy poznáte něco nového a něco nového se naučíte &#8211; prožijete a získáte!</p>\n<p>Na bubnování ve tmě získáváte sebevědomí! Intenzivně probouzíte svůj život! Doslova. Tečka.</p>\n<h3>Bubny se stále častěji stávají součástí veřejných akcí</h3>\n<p>S námi hrajete na obrovské djembe a mohutné basáky doundouny [dunduny], což jsou bubny přivezené ze západní Afriky, konkrétně z Guiney a Mali.</p>\n<p>Milujeme mohutnost rytmu a vibrací, které vytvoří skupina 10 a více bubeníků. Ten pocit zvukové vlny procházející tělem!</p>\n<p><a href=\"http://laskyplnysvet.cz/stesti/wp-content/uploads/2017/03/Bubnovani_ve_tme_divadlo_ABC_a.jpg\"><img class=\"aligncenter size-full wp-image-2817\" src=\"http://laskyplnysvet.cz/stesti/wp-content/uploads/2017/03/Bubnovani_ve_tme_divadlo_ABC_a.jpg\" alt=\"\" width=\"960\" height=\"540\" srcset=\"https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/03/Bubnovani_ve_tme_divadlo_ABC_a.jpg 960w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/03/Bubnovani_ve_tme_divadlo_ABC_a-300x169.jpg 300w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/03/Bubnovani_ve_tme_divadlo_ABC_a-768x432.jpg 768w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/03/Bubnovani_ve_tme_divadlo_ABC_a-600x338.jpg 600w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/03/Bubnovani_ve_tme_divadlo_ABC_a-520x293.jpg 520w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/03/Bubnovani_ve_tme_divadlo_ABC_a-740x416.jpg 740w\" sizes=\"(max-width: 960px) 100vw, 960px\" /></a></p>\n<div id='gallery-1' class='gallery galleryid-2809 gallery-columns-2 gallery-size-medium'><figure class='gallery-item'>\n\t\t\t<div class='gallery-icon landscape'>\n\t\t\t\t<a href='https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_djembe.jpg'><img width=\"300\" height=\"200\" src=\"https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_djembe-300x200.jpg\" class=\"attachment-medium size-medium\" alt=\"\" aria-describedby=\"gallery-1-1916\" srcset=\"https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_djembe-300x200.jpg 300w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_djembe-768x512.jpg 768w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_djembe.jpg 1024w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_djembe-600x400.jpg 600w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_djembe-390x260.jpg 390w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_djembe-520x347.jpg 520w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_djembe-740x494.jpg 740w\" sizes=\"(max-width: 300px) 100vw, 300px\" /></a>\n\t\t\t</div>\n\t\t\t\t<figcaption class='wp-caption-text gallery-caption' id='gallery-1-1916'>\n\t\t\t\tMohutné africké Djembe\n\t\t\t\t</figcaption></figure><figure class='gallery-item'>\n\t\t\t<div class='gallery-icon landscape'>\n\t\t\t\t<a href='https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_doundouny.jpg'><img width=\"300\" height=\"201\" src=\"https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_doundouny-300x201.jpg\" class=\"attachment-medium size-medium\" alt=\"\" aria-describedby=\"gallery-1-1915\" srcset=\"https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_doundouny-300x201.jpg 300w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_doundouny-768x514.jpg 768w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_doundouny-1024x685.jpg 1024w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_doundouny-600x402.jpg 600w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_doundouny-390x260.jpg 390w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_doundouny-520x348.jpg 520w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_doundouny-740x495.jpg 740w, https://laskyplnysvet.cz/stesti/wp-content/uploads/2017/01/laskyplne_bubnovani_meditace_doundouny.jpg 1200w\" sizes=\"(max-width: 300px) 100vw, 300px\" /></a>\n\t\t\t</div>\n\t\t\t\t<figcaption class='wp-caption-text gallery-caption' id='gallery-1-1915'>\n\t\t\t\tBasové africké Dundouny\n\t\t\t\t</figcaption></figure>\n\t\t</div>\n\n<h2>Poznejte bubnování ve tmě!</h2>\n<p><strong>Kde:<br />\n</strong>Pražské zkušebny,<br />\n<em>přesné místo bude upřesněno týden předem, dle počtu účastníků</em></p>\n<p><strong>Kdy:<br />\n<span style=\"color: #ff0000;\">6. řijna, 2018</span></strong><br />\n<strong><span style=\"color: #ff0000;\">14:00 &#8211; 17:00 hodin</span></strong></p>\n<p><strong>Jak:</strong></p>\n<p>S bubny se sžijeme během 1,5 hodiny<br />\na další 1,5 hodiny si je zcela užijeme ve tmě.</p>\n<p><strong>Cena:<br />\n</strong>S půjčením bubnu 990 Kč<br />\nS vlastním bubnem 690Kč</p>\n<h2 style=\"text-align: center;\"></h2>\n<h2 style=\"text-align: center;\"></h2>\n<h2 style=\"text-align: center;\">Rezervace</h2>\n<p style=\"text-align: center;\"><div role=\"form\" class=\"wpcf7\" id=\"wpcf7-f2810-o2\" lang=\"cs-CZ\" dir=\"ltr\">\n<div class=\"screen-reader-response\"></div>\n<form action=\"/stesti/wp-json/wp/v2/posts?per_page=20&#038;offset=0&#038;_embed=true#wpcf7-f2810-o2\" method=\"post\" class=\"wpcf7-form\" novalidate=\"novalidate\">\n<div style=\"display: none;\">\n<input type=\"hidden\" name=\"_wpcf7\" value=\"2810\" />\n<input type=\"hidden\" name=\"_wpcf7_version\" value=\"5.0.2\" />\n<input type=\"hidden\" name=\"_wpcf7_locale\" value=\"cs_CZ\" />\n<input type=\"hidden\" name=\"_wpcf7_unit_tag\" value=\"wpcf7-f2810-o2\" />\n<input type=\"hidden\" name=\"_wpcf7_container_post\" value=\"0\" />\n</div>\n<p><center>Chci zažít bubnování ve tmě!</p>\n<p>Mé jméno je<br />\n<span class=\"wpcf7-form-control-wrap your-name\"><input type=\"text\" name=\"your-name\" value=\"\" size=\"40\" class=\"wpcf7-form-control wpcf7-text wpcf7-validates-as-required\" aria-required=\"true\" aria-invalid=\"false\" /></span> </p>\n<p>Můj email<br />\n<span class=\"wpcf7-form-control-wrap your-email\"><input type=\"email\" name=\"your-email\" value=\"\" size=\"40\" class=\"wpcf7-form-control wpcf7-text wpcf7-email wpcf7-validates-as-required wpcf7-validates-as-email\" aria-required=\"true\" aria-invalid=\"false\" /></span> </p>\n<p>Je libo půjčit buben?<br />\n<span class=\"wpcf7-form-control-wrap buben-828\"><span class=\"wpcf7-form-control wpcf7-radio\"><span class=\"wpcf7-list-item first\"><input type=\"radio\" name=\"buben-828\" value=\"Ano\" checked=\"checked\" /><span class=\"wpcf7-list-item-label\">Ano</span></span><span class=\"wpcf7-list-item last\"><input type=\"radio\" name=\"buben-828\" value=\"Přinesu si svůj\" /><span class=\"wpcf7-list-item-label\">Přinesu si svůj</span></span></span></span>\n</p>\n<p>\n<center><br />\nVše si domluvíme obratem přes email, na který vám přijde do chviličky zpráva. <i>Pro jistotu si zkontrolujte složku hromadné či spam.</i><br />\n</center>\n</p>\n<p>\n<i><small><br />\n<b><br />\n<span class=\"wpcf7-form-control-wrap checkbox-478\"><span class=\"wpcf7-form-control wpcf7-checkbox wpcf7-validates-as-required\"><span class=\"wpcf7-list-item first last\"><input type=\"checkbox\" name=\"checkbox-478[]\" value=\"(Povinné) Souhlasím s Obchodními podmínkami a správou osobních údajů\" checked=\"checked\" /><span class=\"wpcf7-list-item-label\">(Povinné) Souhlasím s Obchodními podmínkami a správou osobních údajů</span></span></span></span><a href=\"https://laskyplnysvet.cz/stesti/obchodni-podminky/\" target=\"_blank\"> (zde) </a><br />\n<span class=\"wpcf7-form-control-wrap checkbox-78\"><span class=\"wpcf7-form-control wpcf7-checkbox\"><span class=\"wpcf7-list-item first last\"><input type=\"checkbox\" name=\"checkbox-78[]\" value=\"Přeji si dostávat dárky a zajímavé informace formou newsletteru\" checked=\"checked\" /><span class=\"wpcf7-list-item-label\">Přeji si dostávat dárky a zajímavé informace formou newsletteru</span></span></span></span><br />\n</b><br />\n</small></i>\n</p>\n<p><br>\n<p>Zaškrtněte políčko<br/></p>\n<div class=\"wpcf7-form-control-wrap\"><div data-sitekey=\"6LdOj0UUAAAAALWa4lgRHOfgNQP1m3n544F_mgQc\" data-size=\"compact\" class=\"wpcf7-form-control g-recaptcha wpcf7-recaptcha\"></div>\n<noscript>\n\t<div style=\"width: 302px; height: 422px;\">\n\t\t<div style=\"width: 302px; height: 422px; position: relative;\">\n\t\t\t<div style=\"width: 302px; height: 422px; position: absolute;\">\n\t\t\t\t<iframe src=\"https://www.google.com/recaptcha/api/fallback?k=6LdOj0UUAAAAALWa4lgRHOfgNQP1m3n544F_mgQc\" frameborder=\"0\" scrolling=\"no\" style=\"width: 302px; height:422px; border-style: none;\">\n\t\t\t\t</iframe>\n\t\t\t</div>\n\t\t\t<div style=\"width: 300px; height: 60px; border-style: none; bottom: 12px; left: 25px; margin: 0px; padding: 0px; right: 25px; background: #f9f9f9; border: 1px solid #c1c1c1; border-radius: 3px;\">\n\t\t\t\t<textarea id=\"g-recaptcha-response\" name=\"g-recaptcha-response\" class=\"g-recaptcha-response\" style=\"width: 250px; height: 40px; border: 1px solid #c1c1c1; margin: 10px 25px; padding: 0px; resize: none;\">\n\t\t\t\t</textarea>\n\t\t\t</div>\n\t\t</div>\n\t</div>\n</noscript>\n</div>\n<p><input type=\"submit\" value=\"Odes  ílám pro rezervaci\" class=\"wpcf7-form-control wpcf7-submit\" /></p>\n<p><br><br />\n</center></p>\n<div class=\"wpcf7-response-output wpcf7-display-none\"></div></form></div>\n<p style=\"text-align: center;\"><a href=\"http://laskyplnysvet.cz/stesti/wp-content/uploads/2017/03/bubnovaci_laskyplnysvet_cz_2017_1_2_big.gif\"><img class=\"aligncenter size-full wp-image-2823\" src=\"http://laskyplnysvet.cz/stesti/wp-content/uploads/2017/03/bubnovaci_laskyplnysvet_cz_2017_1_2_big.gif\" alt=\"\" width=\"480\" height=\"480\" /></a></p>\n<p style=\"text-align: right;\">Foto <a href=\"https://www.flickr.com/photos/ferran-jorda/3442527910/in/photolist-6fcRBu-fsjr-zYauT-4Eqayg-6CAo6V-5Sj9xH-2dB4DN-6wKoBT-eBJ9Es-eBJ9JC-5262Yi-q1jH2p-a5XAue-4PZiJD-bkJMN-agLfsy-fdHPJH-5Sj8AR-9fyhkk-55KRai-58xLSD-5Sj92X-bFXhkg-ocFQSp-5f269q-6JKxeJ-sGrfs-4Xts3s-gmcXy-6EtRT1-DPyKc-bnFuAe-gNRzW-4Xpbdv-b6gXXe-6aQngX-66KiL-6h1CSd-bnFuGa-cA2BV1-6CAmAX-6CAvHc-edHTsW-bnFux2-a61tiQ-6pRSjL-a5XBeT-bnFugg-jZXu-5QRAgD\" target=\"_blank\" rel=\"noopener noreferrer\">flickr</a>, <a href=\"https://www.flickr.com/photos/cadena/2784070436/in/photolist-5f269q-6JKxeJ-sGrfs-4Xts3s-gmcXy-6EtRT1-DPyKc-bnFuAe-gNRzW-4Xpbdv-b6gXXe-6aQngX-66KiL-6h1CSd-bnFuGa-cA2BV1-6CAmAX-6CAvHc-edHTsW-bnFux2-a61tiQ-6pRSjL-a5XBeT-bnFugg-jZXu-5QRAgD-a61tsf-6naotr-gh3YC-a5XAWR-asxYiB-a5XBra-a61skQ-66fvA-6Bt2Dz-2zzTv-8ENj79-a5XANa-DTPXm-26YCeC-DTPne-5DFc8R-5DKs1j-5DFaQK-5DFere-5ZTRYL-8EK9KF-mE7WJ-8EK8nr-5TD3tc\" target=\"_blank\" rel=\"noopener noreferrer\">flickr</a>, <a href=\"https://www.flickr.com/photos/umclix/9047240937/in/photolist-RAWn4z-eMEQAj-eMEZbQ-5j1YmK-eMttwH\" target=\"_blank\" rel=\"noopener noreferrer\" class=\"broken_link\">flickr</a></p>\n<p style=\"text-align: left;\"><strong>Příběh o vzniku myšlenky projektu si můžete přečíst: <a href=\"https://laskyplnysvet.cz/stesti/nalezeni-sama-sebe-skrze-bubny/\" target=\"_blank\" rel=\"noopener noreferrer\">ZDE</a></strong></p>\n<p style=\"text-align: left;\"><strong>Jaký t</strong><strong>o je zážitek ?</strong></p>\n<blockquote>\n<p style=\"text-align: left;\"><em>&#8220;Miluji tmu, tak nějak přirozeně. Delší pobyt ve tmě, spánek při úplně zataženém okně, to vše je moje, člověk se na sebe napojí, stává se tmou, prázdnotou, forma se rozpouští a zůstává pouze naše podstata, vědomí. </em><em>Bubnování ve tmě? Bez váhání jsem se přihlásil a věděl, že si to užiji. Stát se samotným zvukem, naladit se na vlny rytmu i vědomí druhých, stát se jedním, bez studu a strachu. Nikdo mě přeci nevidí. Zážitek nad očekávání. Doporučuji.&#8221;<br />\n</em>Jirka</p>\n</blockquote>\n<blockquote>\n<p style=\"text-align: left;\"><em>&#8220;Pro mne to byl nádherný prožitek společného setkání v rytmu bubnů. Poprvé bubnování ve tmě. Stálo to za to. :-)&#8221;<br />\n</em>Martin</p>\n</blockquote>\n<blockquote>\n<p style=\"text-align: left;\"><em>&#8220;Bubeníků jsem se bál. Ten rytmus, který nemám&#8230; Ale když jsem se konečně odhodlal, tak s vámi jsem ve tmě poznal novou rodinu. Děkuji!&#8221;<br />\n</em>Jindřich</p>\n</blockquote>\n<blockquote>\n<p style=\"text-align: left;\"><em>&#8220;Viděla jsem ve tmě spousty barev :-)&#8221;</em><br />\nTerka</p>\n</blockquote>\n<blockquote>\n<p style=\"text-align: left;\"><em>&#8220;Na bubnování jsem levá, ale ve tmě jsem ožila&#8221;</em><br />\nJana</p>\n</blockquote>\n"
        
        
